{
  "name": "Test Tracker",
  "custom_fields": [
    {
      "field_name": "test",
      "display_label": "How test were you today?",
      "options": [
        {
          "option_name": "level",
          "option_type": "single_choice",
          "choices": ["very_low", "low", "moderate", "high", "very_high"],
          "choice_labels": {
            "very_low": "Very Low",
            "low": "Low",
            "moderate": "Moderate",
            "high": "High",
            "very_high": "Very High"
          },
          "is_required": true
        },
        {
          "option_name": "test_completed",
          "option_type": "number_input",
          "min_value": 0,
          "max_value": 50,
          "is_required": false
        }
      ]
    },
    {
      "field_name": "test_quality",
      "display_label": "How was your focus quality?",
      "options": [
        {
          "option_name": "rating",
          "option_type": "rating",
          "min_value": 1,
          "max_value": 10,
          "is_required": true
        },
        {
          "option_name": "distractions",
          "option_type": "multiple_choice",
          "choices": ["phone", "social_media", "noise", "people", "thoughts"],
          "choice_labels": {
            "phone": "Phone notifications",
            "social_media": "Social media",
            "noise": "Environmental noise",
            "people": "People interruptions",
            "thoughts": "Racing thoughts"
          },
          "is_required": false
        }
      ]
    }
  ]
}

